class Solution {
public:
    int modulo = 1e9 + 7;
    int solver(int n, bool usedTromino, vector<vector<int>> &dp) {
        if(n < 0) return 0;
        if(n == 0) {
            if(usedTromino) return 0;
            return 1;
        } 

        if(dp[n][usedTromino] != -1) return dp[n][usedTromino];

        long long way1 = 0 , way2 = 0 , way3 = 0, way4 = 0;

        // Using Dominos
        if(n >= 2 && !usedTromino) way1 = solver(n - 2, usedTromino, dp);
        if(n >= 1) way2 = solver(n - 1, usedTromino, dp);

        // Using Trominos . These must be used as a pair i.e even no. times because using a tromino but not coupling with an opposite tromino  will result in a tile being uncovered.

        // Opening Tromino 
        if(n >= 3 && !usedTromino) way3 = 2 * solver(n - 2, !usedTromino, dp);
        // Closing Tromino to fit into the space left by opening tromino
        if(n >= 1 && usedTromino) way4 = solver(n - 1, !usedTromino, dp);

        return dp[n][usedTromino] = (way1 + way2 + way3 + way4) % modulo;
    }

    int numTilings(int n) {
        vector<vector<int>> dp(n  + 1, vector<int>(2, -1));

        return solver(n, false, dp);
    }
};

