class Solution {
public:
    int numTilings(int n) {
        int md = 1e9;
        md += 7;

        vector<long long> v(1001,0);

        v[0] = 0;
        v[1] = 1;
        v[2] = 2;
        v[3] = 5;

        if(n <= 3)
            return v[n];

        for(int i = 4; i <=n;i++)
        {
            v[i] = 2*v[i-1] + v[i-3];
            v[i] = v[i] % md;
        }    
        return v[n];

    }
};
