# Code
```cpp
class Solution {
public:
    int numTilings(int n) {
        // we can solve this problem using dynamic programming
        int MOD = 1e9 + 7;
        vector<vector<int>> memo(n+1, vector<int>(2, -1));

        function<int(int,bool)> solve = [&](int i, bool flag) {
            // if we go out of bounds
            if(i < 0) return 0;

            // if we tiled the entire board
            if(i == 0) return (int)!flag;

            // if we memoized the current state
            if(memo[i][flag] != -1) return memo[i][flag];

            long result = 0;

            // if we have 1 cell covered in the current column
            if(flag) {
                // we can use a horizontal domino
                long val1 = solve(i-1, true);

                // we can use a tromino
                long val2 = solve(i-2, false);

                result = val1 + val2;
            }
            else {
                // we can use a vertical domino
                long val1 = solve(i-1, false);

                // we can use 2 horizontal dominos
                long val2 = solve(i-2, false);

                // we can use a tromino
                // there are two ways of placing it
                long val3 = 2*solve(i-1, true);

                result = val1 + val2 + val3;
            }

            return memo[i][flag] = result%MOD;
        };

        // start with an empty board
        return solve(n, false);
    }
};
```

