Better than 100%  submissions

	class Solution
	{
	public:
		long long dp[1001][4];
		const int mod = 1e9 + 7;
		int numTilings(int n)
		{
			memset(dp, 0, sizeof(dp));
			dp[0][3] = 1;
			for (int i = 1; i <= n; i++)
			{
				for (int j = 0; j < 4; j++)
				{
					switch (j)
					{
					case 0:
						dp[i][j] = dp[i - 1][3];
						break;
					case 1:
						dp[i][j] = dp[i - 1][2] + dp[i - 1][0];
						break;
					case 2:
						dp[i][j] = dp[i - 1][1] + dp[i - 1][0];
						break;
					case 3:
						dp[i][j] = dp[i - 1][0] + dp[i - 1][3] + dp[i - 1][2] + dp[i - 1][1];
					}
					dp[i][j] %= mod;
				}
			}
			return dp[n][3];
		}
	};

