class Solution {
public:
    int power(int& p)
    {
        int i, a;
        long long ans=1;
        for(i=1;i<=p;i++)
        {
            ans=(ans*2)%1000000007;
        }

        a=ans;

        return a;
    }

    int find_tiles(double length, int p, vector<vector<int>>& dp)
    {
        double zero=0.0;
        if(length==zero)
        {
            int pair=0;
            if(p>0)
            {
                if(p>1)
                {
                    double p1, p2=p;
                    p1=power(p);
                    pair=p1;

                    return dp[length][p]= pair;
                }

                return dp[length][p]=2;
            }

            return dp[length][p]=1;
        }

        int len_in=length;

        if(length - len_in == 0 && dp[length][p] != -1)
        {
            return dp[length][p];
        }

        int c1=0, c2=0, c3=0;

        if( !(length - len_in > 0) && (length-2.0 >= zero))
        {
            c1=(find_tiles(length-2, p, dp))%1000000007;
        }



        if(length-1.5 >= zero)
        {
            if(length - len_in > 0)
            {
                c2=(find_tiles(length-1.5, p+1, dp))%1000000007;
            }
