        n=piles.size();
        memset(dp,-1,sizeof(dp));
        int sum= accumulate(piles.begin(),piles.end(),0);
        //cout<<sum<<" "<<dfs(piles,0,1);
        return (sum+dfs(piles,0,1))/2;
