public int stoneGameII(int[] piles) {
		// calculate the sum from 0 to current index i
        for(int i=1;i<piles.length;i++)
            piles[i]=piles[i-1]+piles[i];
	    // use HashMap to cache the max value that the adversary can get, which is associated with M plus current position i
        HashMap<String,Integer> cache=new HashMap<>();
        return dfs(1,0,piles,cache);
    }
    int dfs(int M, int i, int[] piles,HashMap<String,Integer> cache){
		// base case
        if(piles.length==i) return 0;
        if(cache.containsKey(M+" "+i)) return cache.get(M+" "+i);
		// 'max' refers to the max value that the current player can get
        int max=-1;
		// because we start from index i, we calculate the max value from i to end, therefore, we need to calculate the sum from i to end, the sum of (i, end) if piles[piles.length-1]-preSum 
        int preSum=i-1<0?0:piles[i-1];
		// try every possibility, [1:2M]
        for(int k=0;k<=2*M-1;k++){
            if(k+i==piles.length) break;
			// this is the value that current player can get from next round, which is returned by opponent 
            int next=dfs(Math.max(k+1,M),i+k+1,piles,cache);
            int count=next+piles[k+i]-preSum;
			// find the max value
            max=Math.max(max, count);
        }
		// this is the value that opponent can get 
        int nextValue=piles[piles.length-1]-preSum-max; 
		// cache
        cache.put(M+" "+i,nextValue);
        if(i==0) return max;
        return nextValue;
    }
