class Solution {
    int stoneGameIIHelp(int ind, int m, vector<vector<int>> &dp, vector<int> &preSum){
        if( ind >= preSum.size() ) return 0;
        if( dp[ind][m] != -1 ) return dp[ind][m];

        int maxi = 0, n = preSum.size();
        for(int x=1; x<=min(2*m, n-ind); x++)
            maxi = max(maxi, preSum[ind] - stoneGameIIHelp(ind+x, max(m, x), dp, preSum));

        return dp[ind][m] = maxi;
    }

public:
    // TOP DOWN
    int stoneGameII1(vector<int>& ps) {
        int n = ps.size();
        for(int i=n-2; i>=0; i--) ps[i] += ps[i+1];
        vector<vector<int>> dp(n, vector<int>(n+1, -1));
        return stoneGameIIHelp(0, 1, dp, ps); 
    }


    // Bottom Up
    int stoneGameII(vector<int>& preSum) {
        int n = preSum.size();
        for(int i=n-2; i>=0; i--) preSum[i] += preSum[i+1];
        vector<vector<int>> dp(n+1, vector<int>(n+1));

        for(int ind=n-1; ind>=0; ind--){
            for(int m = n; m>=1; m--){
                int maxi = 0;
                for(int x=1; x<=min(2*m, n-ind); x++)
                    maxi = max(maxi, preSum[ind] - dp[ind+x][max(m, x)]);

                dp[ind][m] = maxi;
            }
        }
        return dp[0][1];
    }
};
