class Solution {
public:
    int dp[100][100];
    int stoneGameII(vector<int>& piles) {
      int N = piles.size();
      memset(dp, 0, sizeof(dp));

      // update piles[i] to sum of i to N stones
      for ( int i = N-2; i >= 0; i--)
        piles[i] += piles[i+1];

      dfs(0, 1, N, piles);
      return dp[0][1];

    }
  void dfs(int i, int m, int N, vector<int>& piles) {
    // if we can take everything, do so
    if (m*2 + i >= N) {
      dp[i][m] = piles[i]; 
      return;
    }
    if (dp[i][m] != 0) return;

    int ans = 0;
    // try all possible j values to find the one that maximizes current players count
    for (int j = 1; j <= m*2; j++) {
      dfs( i+j, max(m , j), N, piles);
      ans = max(ans, piles[i] - dp[i+j][max(m,j)]);
    }
    dp[i][m] = ans;
  }
};
