class Solution(object):
    def stoneGameII(self, piles):
        """
        :type piles: List[int]
        :rtype: int
        """
        # Intuition: We are trying to find out how much maximum Alex can score with this program
        # So we try to maximise Alex's score when it is Alex's turn
        # When it is Lee's turn, he will try to maximise his score and since we are returning just Alex's score, we need to minimize
        # Alex's score when it is Lee's turn.

        def recursion(arr, low, high, m, is_it_alex_turn):
            # Start with base case:
            # If there are less than 2 * m piles remaining, Alex or Lee will always take all of it
            # If it is Alex's turn, he takes full and we return sum of all piles
            # If it is Lee's turn, he takes full and we return 0 because this program is tracking Alex's score. Which would be zero if Lee takes it all. 

            if high - low + 1 <= 2 * m:
                if is_it_alex_turn:
                    return sum(arr[low: high + 1])
                else:
                    return 0


            if is_it_alex_turn:
                # if it is Alex's turn, he will try to maximise his own return
                cur_score = 0
                for k in range(1, 2 * m + 1):
                    score = sum(arr[low : low + k]) + recursion(arr, low + k, high, max(m, k), not is_it_alex_turn)
                    cur_score = max(cur_score, score)

                return cur_score
            else:
                # It is Lee's turn who will try to maximise his own return by decreasing Alex's score.
                # Because we are trying to return just Alex's score, we decrease Alex's score instead by choosing minimum here
                cur_score = sys.maxint
                for k in range(1, 2 * m + 1):
                    score = 0 + recursion(arr, low + k, high, max(m, k), not is_it_alex_turn)
                    cur_score = min(cur_score, score)

                return cur_score

        def memoization(arr, low, high, m, is_it_alex_turn):
            if high - low + 1 <= 2 * m:
                if is_it_alex_turn:
                    return sum(arr[low: high + 1])
                else:
                    return 0

            if memoize[low][m][is_it_alex_turn] is not None:
                return memoize[low][m][is_it_alex_turn]

            if is_it_alex_turn:
                # if it is Alex's turn, he will try to maximise his own return
                cur_score = 0
                for k in range(1, 2 * m + 1):
                    score = sum(arr[low : low + k]) + memoization(arr, low + k, high, max(m, k), is_it_alex_turn ^ 1)
                    cur_score = max(cur_score, score)

                memoize[low][m][is_it_alex_turn] = cur_score
                return cur_score
            else:
                # It is Lee's turn who will try to maximise his own return by decreasing Alex's score.
                # Because we are trying to return just Alex's score, we decrease Alex's score instead by choosing minimum here
                cur_score = sys.maxint
                for k in range(1, 2 * m + 1):
                    score = 0 + memoization(arr, low + k, high, max(m, k), is_it_alex_turn ^ 1)
                    cur_score = min(cur_score, score)

                memoize[low][m][is_it_alex_turn] = cur_score
                return cur_score


        # m = 1
        # is_it_alex_turn = True
        # return recursion(piles, 0, len(piles) - 1, m, is_it_alex_turn)

        memoize = [[[None for _ in range(0, 2)] for _ in range(3 * len(piles))] for _ in range(len(piles))]
        m = 1
        is_it_alex_turn = 1
        return memoization(piles, 0, len(piles) - 1, m, is_it_alex_turn)
