class Solution {
public:
  int stoneGameII(vector<int>& piles) {
    const int S = std::accumulate(piles.begin(), piles.end(), 0);
    const int N = piles.size();
    // score[i][m] := the best score the player can get when facing index i of the piles
    // and with the m value set.
    // `score` means the *difference* between the stone this player can get and the next
    // player.
    vector<vector<int>> scores(N + 1, vector<int>(N + 1, -S));
    for (int m = 1; m <= N; ++m) {
      scores.back()[m] = 0;
    }
    for (int i = N - 1; i >= 0; --i) {
      vector<int>& i_score = scores[i];
      int stone = 0;
      for (int j = i; j < N; ++j) {
        stone += piles[j];
        int taken = j - i + 1;
        for (int m = (taken + 1) / 2; m <= N; ++m) {
          int future_m = max(m, taken);
          i_score[m] = max(i_score[m], stone - scores[j + 1][future_m]);
        }
      }
    }
    return (scores[0][1] + S) / 2;
  }
};
```

