class Solution {
public:
    int dp[101][101] = {[0 ... 100][0 ... 100] = -1};
    int dfs (int i, int M, vector<int>& piles, int sum) {
        int ret = 0, n = piles.size(), e = i + 2 * M;
        if (e >= n) return sum;
        if (dp[i][e] >= 0) return dp[i][e];
        for (int j = i, nsum = sum; j < e; j++) {
            M = max(M, j - i + 1);
            nsum -= piles[j];
            ret = max(ret, sum - dfs(j + 1, M, piles, nsum));
        }
        return dp[i][e] = ret;
    }
    int stoneGameII(vector<int>& piles) {
        return dfs(0, 1, piles, accumulate(piles.begin(), piles.end(), 0));
    }
};
