class Solution {
public:
/*
Logic:Simple recursion will give you tle ,so we will go with memorizing steps.
Now here we are calculating the difference in the scores of alice and bob , 
i.e we are playing optimally for both alice and bob, so what we will be doing is just
adding the current pile score to total and playing optimally for opponent player and calculating the difference in their score.
so let us supoose we have alice score->A and bob score->B then from 
helper function we are getting A-B and we know that sum of piles=A+B 
then A=((A+B)+(A-B))/2.....
*/
    int dp[101][202];
    int helper(int i,int M,vector<int>&piles){
        //base case
        if(i>=piles.size())
            return 0;
        //memorised step
        if(dp[i][M]!=-1)
            return dp[i][M];
        //calculate total for current player and let opponent play for themself.
        int tot=0;
        //initialize ans for comparing ans and having maximum.
        int ans=INT_MIN;
        for(int j=0;j<2*M;j++){
            if(i+j<piles.size())
                tot+=piles[i+j];
            ans=max(ans,tot-helper(i+j+1,max(M,j+1),piles));
        }
        return dp[i][M]=ans;
    }


    int stoneGameII(vector<int>& piles) {
        //initialise dp with -1.
        memset(dp,-1,sizeof dp);
        int sum=0;
        for(int i=0;i<piles.size();i++)
            sum+=piles[i];
        int diff=helper(0,1,piles);
        return (sum+diff)/2;
    }
};
```

