class Solution {
public:

    int dp[101][201][2];
    int points(vector<int>& piles, int start, int M, bool alice){
        int n = piles.size();
        if(start>=n) return 0;
        if(dp[start][M][alice]!=-1) return dp[start][M][alice];
        int pointsgot;
        if(alice){
            pointsgot = INT_MIN;
            int temppoints = 0;
            for(int x=start; x<min(2*M+start,n); x++){
                temppoints += piles[x];
                pointsgot = max(pointsgot, temppoints+points(piles,x+1,max(M,x+1-start),false));
            }
        }
        else{
            pointsgot = INT_MAX;
            for(int x=start; x<min(2*M+start,n); x++){
                pointsgot = min(pointsgot, points(piles,x+1,max(M,x+1-start),true));
            }
        }
        dp[start][M][alice] = pointsgot;
        return pointsgot;
    }

    int stoneGameII(vector<int>& piles) {
        for(int i=0; i<101; i++)
            for(int j=0; j<201; j++)
                for(int k=0; k<2; k++)
                    dp[i][j][k]=-1;
        return points(piles, 0, 1, true);
    }
};
