class Solution {
    public int stoneGameII(int[] ps) {
        int n = ps.length, sum = 0;
        Integer dp[][] = new Integer[n + 1][n / 2 + 2]; //dp[i][j] means max winning stone when first i taken, and M == j;
        for (int p : ps) sum += p;
        return (sum + dfs(dp, ps, 0, 1, n)) / 2; 
    } 

    private int dfs(Integer[][] dp, int[] ps, int taken, int m, int n) { 
        int res = Integer.MIN_VALUE, sum = 0;
        if (taken >= n) return 0;
        if (dp[taken][m] != null) return dp[taken][m];
        for (int i = 1; i <= m * 2 && taken + i - 1 < n; i++) {
            sum += ps[taken + i - 1];
            res = Math.max(res, sum - dfs(dp, ps, taken + i, Math.max(m, i), n));
        }
        dp[taken][m] = res;
        return res;
    }
}
