#### Bottom-up
```
class Solution {
public:
    // Time: O(n^3)
    // Space: O(n^2)
    int stoneGameII(vector<int>& piles) {
        int n = piles.size();
        vector<int> suf_sum(n, 0);
        for (int i = n - 1; i >= 0; i--) {
            if (i == n - 1) suf_sum[i] = piles[i];
            else suf_sum[i] = suf_sum[i + 1] + piles[i];
        }
        vector<vector<int>> dp(n, vector<int>(n + 1, 0));
        for (int i = n - 1; i >= 0; i--) {
            for (int M = n; M >= 1; M--) {
                for (int x = 1; x <= 2 * M; x++) {
                    int opt;
                    if (i + x >= n) opt = suf_sum[i];
                    else opt = suf_sum[i] - dp[i + x][max(x, M)];
                    dp[i][M] = max(dp[i][M], opt);
                }
            }
        }
        return dp[0][1];
    }
};
