class Solution2 {
public:
    // Time: O(n^3)
    // Space: O(n^2)
    int stoneGameII(vector<int>& piles) {
        int n = piles.size();
        vector<int> suf_sum(n, 0);
        for (int i = n - 1; i >= 0; i--) {
            if (i == n - 1) suf_sum[i] = piles[i];
            else suf_sum[i] = suf_sum[i + 1] + piles[i];
        }
        vector<vector<int>> dp(n, vector<int>(n, 0));
        return helper(piles, 0, 1, dp, suf_sum);
    }
    int helper(vector<int> piles, int i, int M, vector<vector<int>>& dp, const vector<int>& suf_sum) {
        if (2 * M >= piles.size() - i) {
            return suf_sum[i];
        }
        if (dp[i][M] != 0) return dp[i][M];
        int res = INT_MAX;
        for (int x = 1; x <= 2 * M; x++) {
            int new_m = max(M, x);
            res = min(res, helper(piles, i + x, new_m, dp, suf_sum));
        }
        dp[i][M] = suf_sum[i] - res;
        return dp[i][M];
    }
};
```

