class Solution:
    def stoneGameII(self, piles: List[int]) -> int:
        def dfs(total, start, M, cache):
            key = (M, start)
            if not key in cache:
                if start >= len(piles):
                    cache[key] = 0
                else:
                    cur_sum = 0
                    for x in range(1, 2*M+1):
                        index = start + (x-1)
                        if index < len(piles):
                            cur_sum += piles[index]
                            cache[key] = max(cache[key], total - dfs(total-cur_sum, index+1, max(M, x), cache))
            return cache[key]

        total = sum(piles)
        return dfs(total, 0, 1, collections.defaultdict(int))
