```java
class Solution {
    public int stoneGameII(int[] piles) {
        int sum = 0;
        for(int pile : piles) {
            sum += pile;
        }

        Map<String, Integer> map = new HashMap<>();

        return dfs(piles, sum, 0, 1, map);
    }

	// max value we can get if we start at idx with m
    private int dfs(int[] piles, int sum, int idx, int m, Map<String, Integer> map) {
        if(idx >= piles.length) {
            return 0;
        }

        if(map.containsKey(("" + idx) + m)) {
            return map.get(("" + idx) + m);
        }

        int max = 0;
        int choose = 0;
        for(int i = idx; i < idx + 2 * m && i < piles.length; i++) {
			//we choose stones from idx to i 
            choose += piles[i];

			// sum: sum of stones left
			// sum - dfs(...) is the value we can get in this turn.
			// the next player also plays optimally
            max = Math.max(max, sum - dfs(piles, sum - choose, i + 1, Math.max(i - idx + 1, m), map));
        } 

        map.put(("" + idx) + m, max);

        return max;
    }
}
