class Solution:
    def stoneGameII(self, A: List[int]) -> int:
        n = len(A)
        for i in range(n - 1):
            A[i + 1] += A[i]
        d = {}
        def helper(i, m):
            if i >= n: return 0
            if (i, m) in d: return d[i, m]
            res = 0
            for x in range(1, min(2 * m + 1, n - i + 1)):
                res = max(res, A[n - 1] - (A[i - 1] if i > 0 else 0) - helper(i + x, max(m, x)))
            d[i, m] = res
            return res
        return helper(0, 1)
