class Solution {
public:
    int alice(vector<int>&p , int idx , int m , vector<vector<vector<int>>>&dp){
        if(idx>=p.size()) return 0;

        if(dp[idx][m][1] != -1) return dp[idx][m][1];

        int ans = 0;
        int sum = 0;
        for(int i = 0;i<(2*m) && (idx + i)<p.size();i++){
            sum += p[idx + i];
            ans = max(ans , sum + bob(p , idx+i+1 , max(i+1 , m) , dp));
        }
        return dp[idx][m][1] = ans;
    }

    int bob(vector<int>&p , int idx , int m , vector<vector<vector<int>>>&dp){
        if(idx>=p.size()) return 0;
        if(dp[idx][m][0] != -1) return dp[idx][m][0];
        int ans = INT_MAX;
        for(int i = 0;i<(2*m) && (idx + i)<p.size();i++){
            ans = min(ans , alice(p , idx+i+1 , max(i+1 , m) , dp));
        }
        return dp[idx][m][0] = ans;
    }

    int stoneGameII(vector<int>& piles) {
        vector<vector<vector<int>>>dp(piles.size() , vector<vector<int>>(piles.size()+1 , vector<int>(2,-1)));
        return alice(piles , 0 , 1 , dp);
    }
};
