        return dfs(0, 0, 1);
    }

    private int dfs(int p, int i, int m) {
        if (i >= piles.length)
            return 0;

        int res = p == 0 ? 0 : 1000000;
        int total = 0;

        for (int x = 1; x <= 2 * m; x++) {
            if (i + x > piles.length)
