use std::collections::HashMap;

impl Solution {
    pub fn stone_game_ii(piles: Vec<i32>) -> i32 {
        fn get_max_at_index_with_m(
            sums: &Vec<i32>,
            m: usize,
            start_index: usize,
            store: &mut HashMap<(usize, usize), i32>,
        ) -> i32 {
            if start_index >= sums.len() {
                return 0;
            }
            let max_take = (2 * m) as usize;
            let max_take = (sums.len() - start_index).min(max_take);
            if let Some(&value) = store.get(&(max_take, start_index)) {
                return value;
            } else {
                let total_sum = sums[start_index];
                let min_next_turn = (1..=max_take)
                    .map(|take| {
                        get_max_at_index_with_m(sums, take.max(m), start_index + take, store)
                    })
                    .min()
                    .unwrap_or_default();
                let value = total_sum - min_next_turn;

                store.insert((max_take, start_index), value);
                value
            }
        }
        let last = piles[piles.len() - 1];
        let sum_i = (0..piles.len() - 1)
            .rev()
            .fold(vec![last; piles.len()], |mut sums, index| {
                sums[index] = sums[index + 1] + piles[index];
                sums
            });
        get_max_at_index_with_m(
            &sum_i,
            1,
            0,
            &mut HashMap::with_capacity(piles.len().pow(2)),
        )
    }
}

