class Solution {
public:

    int dp[101][(int)1e4 + 1];
    int suffSum[101];
    int totalPiles;
    int solve(int idx, int M) {
        if(idx >= totalPiles) { // all piles already covered
            return 0;
        }
        if(2 * M >= totalPiles - idx) {
            return suffSum[idx]; // it would be most optimal for current player to pick all remaining piles
        }
        if(dp[idx][M] != -1) {
            return dp[idx][M];
        }

        int mn = INT_MAX; // pick min stones possible for the other player
        for(int x = 1; x <= 2 * M; x++) {
            mn = min(mn, solve(idx + x, max(x, M)));
        }
        return dp[idx][M] = suffSum[idx] - mn;
    }

    int stoneGameII(vector<int>& piles) {
        memset(dp, -1, sizeof(dp));
        memset(suffSum, 0, sizeof(suffSum));
        int n = piles.size();
        totalPiles = n;
        suffSum[n - 1] = piles[n - 1];
        for(int i = n - 2; i >= 0; i--) {
            suffSum[i] = suffSum[i + 1] + piles[i];
        }
        return solve(0, 1);
    }
};
