class Solution {
    public int stoneGameII(int[] piles) {
        //find presum
        //my max score=presum-opponents lowest score
        if(piles.length==1)
            return piles[0];
        else if(piles.length==2)
            return piles[0]+piles[1];
			//arr is basically presum
        int [] arr=new int[piles.length];
        int [][] dp=new int[piles.length][piles.length];
        arr[arr.length-1]=piles[arr.length-1];
        for(int i=arr.length-2;i>=0;i--){
            arr[i]=piles[i]+arr[i+1];
        }
        Arrays.fill(dp[arr.length-1],piles[arr.length-1]);
        for(int row=arr.length-2;row>=0;row--){
            for(int m=1;m<arr.length;m++){
                int min=100000007;
                if(row+2*m>=arr.length){
                    dp[row][m]=arr[row];
                }else{
                     for(int x=1;x<=2*m && x+row<arr.length;x++){
                    min=Math.min(min,dp[row+x][Math.max(x,m)]);
                    }
                    dp[row][m]=arr[row]-min;
                }
            }
        }
        return dp[0][1];
    }
}

