    public int stone(int [] arr,int n,int i,int M_alice){
        if(dp[i][M_alice]!=-1){
            return dp[i][M_alice];

        }
        //base case 

        if(i==n){
            return 0;
        }
        //X-->1 to 2*M
        int X_alice=Math.min(2*M_alice,n-i),stones_alice=0,res=Integer.MIN_VALUE;
        for(int j=0;j<X_alice;j++){
            //j is the number of piles picled by alice 0-->2*m-1
            stones_alice+=arr[i+j];
            int M_bob=Math.max(M_alice,j+1);
            int X_bob=Math.min(2*M_bob,n-i-j-1);
            int later_stones_alice=Integer.MAX_VALUE;
            for(int k=0;k<X_bob;k++){
                //k --> number of piles picked by bob
                //Bob is trying to minimize the stones picked by alice
                later_stones_alice=Math.min(later_stones_alice,stone(arr,n,i+(j+1)+(k+1),Math.max(M_bob,k+1)));
            }
            if(later_stones_alice!=Integer.MAX_VALUE)
                res=Math.max(res,stones_alice+later_stones_alice);
            else 
                res=Math.max(res,stones_alice);
        }
        dp[i][M_alice]=res;
        return res;



    }
}
