class Solution {
public:
    int turn =1; int n;
    vector<int> p;
    int dp[115][115][3];
    int solve(int m, int pos, int turn){
        if(pos==n) return 0;
        int tm=2*m;
        if(m>=n) m=n-1;
        int ans=0;
        if(dp[m][pos][turn]!=-1){
            return dp[m][pos][turn];
        }
        int res=0; int res1=INT_MAX;
        int pos1=pos;
        for(int i=1;i<=tm&&pos1<n;i++){
            ans+=p[pos1]; pos1++;
            if(turn==1){
                res=max(res,ans+solve(max(i,m),pos1,2));// alice maximizing his score
            }
            else{
                res1=min(res1,solve(max(i,m),pos1,1));// bob minimizing alice's score
            }

        }
        return dp[m][pos][turn]=(turn==1?res:res1);// according to alice/bob

    }
    int stoneGameII(vector<int>& piles) {
        p=piles;
        n=piles.size(); memset(dp,-1,sizeof(dp));
        return solve(1,0,1);
    }
};



