        if(DP[M][idx][chance]!=-1)
            return DP[M][idx][chance];
        if(chance==1)
        {
            int res=0,sum=0;
            for(int j=idx;j<idx+2*M && j<piles.size();j++)
            {
                sum+=piles[j];
                res=max(res,sum+solve(piles,max(M,j-idx+1),j+1,0));
