            int res=solve(piles,M,idx+1,1);
            for(int i=idx+1;i<idx+2*M && i<piles.size();i++)
            {
                res=min(res,solve(piles,max(M,i-idx+1),i+1,1));
            }
            return DP[M][idx][chance]=res;
        }
    }
