class Solution {
    public int stoneGameII(int[] piles) {
        int n= piles.length;
        int [][]dp = new int [n+1][n+1];
        int [] sum = new int [n+1];
        for(int i = n-1;i>=0;i--){
            sum[i] += sum[i+1]+piles[i];
            for(int j = n;j>0;j--){
                int limit = i+2*j-1;
                for(int k  = i;k<=Math.min(limit, n-1);k++)
                    dp[i][j] = Math.max(dp[i][j], sum[i] - dp[k+1][Math.max(j, k-i+1)]);
            }
        }
        return dp[0][1];
    }
}
