class Solution {
public:
    vector<vector<int>> dp; 

    int memo(vector<int> & piles, int start, int M, int n){
        if(start >= n) return 0; 
        if(dp[start][M] != -1) return dp[start][M]; 

        int sum =0, res = INT_MIN; 
        for(int i= start; i< min(n, start+2*M); i++){
            sum += piles[i]; 
            res = max(res, sum-memo(piles, i+1, max(i+1-start, M), n)); 
        }
        return dp[start][M] = res ; 
    }
    int stoneGameII(vector<int>& piles) {
        int n = piles.size(); 
        dp.resize(n+1 ,vector<int> (2*n+1, -1)) ; 
        int sum = 0; 
        for(auto x:piles) sum += x;
        return (sum + (memo(piles, 0, 1, n)))/2;
    }
};
