class Solution {
public:
    int stoneGameII(vector<int>& piles) {
        int size = piles.size();

        // Pile sum from right to left
        vector<int> pileSum(size, piles[size - 1]);
        for (int i = size - 2; i >= 0; --i)
        {
            pileSum[i] = pileSum[i + 1] + piles[i];
        }

        // DP calculation, M rows and piles.size() columns. We need to start from the end until reaching dp[0][0]
        vector<vector<int>> dp(size, vector<int>(size, 0));
        for (int i = size - 1; i >= 0; --i)
        {
            for (int j = size - 1; j >= 0; --j)
            {
                int m = i + 1;
                if (j + 2 * m >= size)
                {
                    dp[i][j] = pileSum[j];
                }
                else
                {
                    // Find maximum amount of stones by picking from 1 to 2 * m
                    int maxStone = 0;
                    for (int k = 1; k <= 2 * m && j + k <= size; ++k)
                    {
                        // For each turn we may need to update m in case k > m
                        int stone = 0, newM = max(k, m);
                        if (newM > size)
                        {
                            break;
                        }

                        for (int l = 0; l < k; ++l)
                        {
                            stone += piles[j + l];
                        }

                        // Since Alice starts first, her opponent (Bob) will get maximum # of stones from next turn,
                        // hence we need to deduct the # of stones Bob has gotten
                        if (j + k < size)
                        {
                            stone += pileSum[j + k] - dp[newM - 1][j + k];
                        }

                        maxStone = stone > maxStone ? stone : maxStone;
                    }

                    dp[i][j] = maxStone;
                }
            }
        }

        return dp[0][0];
    }
};
