class Solution {
    int[] pSumArr ;
    int n ;
    int [][][] cache;
    public int stoneGameII(int[] piles) {
        n = piles.length;
        pSumArr = new int[n+1];
        cache = new int[n][n+1][2];
        for (int i = 0; i < n ; i++) {
            pSumArr[i+1] = pSumArr[i]+piles[i];
        }
        return stoneGameRec(piles,1,1,0);
    }

 private int stoneGameRec(int[] piles,int M, int alice, int i) {
       if(i >= n) return 0;
       if(cache[i][M][alice] != 0) return cache[i][M][alice];

        int sum = 0;
            for (int  x = 1; x <= 2*M &&(i+x <= n); x++) {
             int pSum = getPrefixSum(i, i+x);
             int recSum = stoneGameRec(piles, Math.max(x,M), ((alice ==1)? 0:1),i+x);
             if(alice == 1){
                if(sum == 0) sum = Integer.MIN_VALUE;
                  sum = Math.max(sum,recSum+pSum);
             } else {
                // incase of bob, ignore the pSum and also choose such that
                //  alice gets minimum.
                 if(sum == 0) sum = Integer.MAX_VALUE; 
                 sum =  Math.min(sum,recSum);
            }
        }
      cache[i][M][alice] = sum;
      return sum;
    }

 private  int getPrefixSum(int i , int j ) {
        if(i <=n && j <= n) {
          return pSumArr[j] - pSumArr[i];    
        }
        return 0;
    }
}
