use std::collections::HashMap;

impl Solution {
    pub fn stone_game_ii(piles: Vec<i32>) -> i32 {
        let mut cache = HashMap::new();
        Self::go(&piles[..], 1, &mut cache).0
    }

    fn go<'a>(
        piles: &'a [i32],
        m: usize,
        cache: &mut HashMap<(&'a [i32], usize), (i32, i32)>,
    ) -> (i32, i32) {
        if let Some(result) = cache.get(&(piles, m)) {
            return *result
        }
        let (mut p1, mut p2) = (0, 0);
        for i in 1..=(2 * m).min(piles.len()) {
            let pick: i32 = piles[0..i].iter().sum();
            let (next2, next1) = Self::go(&piles[i..], m.max(i), cache);
            if next1 + pick > p1 {
                p1 = next1 + pick;
                p2 = next2;
            }
        }
        cache.insert((piles, m), (p1, p2));
        (p1, p2)
    }
}
