int stonesAliceGets(vector<int>&piles, vector<vector<int>>&dp, int m, int i, int turn)
    {
        if (i >= piles.size()) return 0;
        if (dp[i][m] != -1) return dp[i][m]; //use stored results

        int takenCount = 0;
        int ans = (turn == 1)? 0 : INT_MAX;
        for (int x = 1; x <= (2 * m); x++)
        {
            int pileIdx = i + x - 1;
            if(pileIdx >= piles.size())break;
            takenCount += piles[pileIdx]; 

            if (turn) // ALICE TURN
            {
                int nextAliceCount = stonesAliceGets(piles, dp, max(m, x), pileIdx + 1, 0);
                int totalCount = takenCount + nextAliceCount; 
                ans = max(ans, totalCount); //maximize what Alice will get
            }
            else   // BOB TURN,,, ALICE WILL TAKE MINIMUM HERE
            {
                int nextAliceCount = stonesAliceGets(piles, dp, max(m, x), pileIdx + 1, 1);
                ans = min(ans, nextAliceCount); //minimize what Alice will get
            }
        }
        return dp[i][m] = ans; //store the results
    }
    int stoneGameII(vector<int>& piles) 
    {
        int n = piles.size();
        vector<vector<int>>dp(n + 1, vector<int>(n+1, -1));
        int ans = stonesAliceGets(piles, dp, 1, 0, 1);
        return ans;

    }

