        if (i >= piles.size())
            return 0;

        if (dp[i][m] != -1)
            return dp[i][m];

        int total = 0;
        int ans = INT_MIN;

        for (int j = 0; j < 2 * m; j++) {
            if (i + j < piles.size())
                total += piles[i + j];

            ans = max(ans, total - helper(i + j + 1, max(m, j + 1), piles));
        }

        return dp[i][m] = ans;
