    const memo = new Map();

    const dfs = (alice: boolean, idx: number, M: number): number => {
        if (idx == piles.length) return 0;

        if (memo.has([alice, idx, M])) return memo.get([alice, idx, M]);

        let result = alice === false? 1000000 : -1,
            total = 0;

        for (let x = 1; x <= 2 * M; x++) {
            if (idx + x > M) break;

            total += piles[idx + x - 1];
            if (alice) {
                result = Math.max(result, total + dfs(false, idx + x, Math.max(M, x)));
            } else {
                result = Math.min(result, dfs(true, idx + x, Math.max(M, x)));
            }
        }

        memo.set([alice, idx, M], result);
        return result;
    };

    return dfs(true, 0, 1);
}
