            set.add(transformMorse(word, arr));
        return set.size();
    }
    private String transformMorse(String word, String[] arr) {
        StringBuilder build = new StringBuilder();
        for (char c : word.toCharArray())
            build.append(arr[(int)c - 97]);
        return build.toString();
    }
}
```

