#define CHAR_SIZE 2

int n = 0;

class Trie {
public:
	bool isLeaf;
	Trie* character[CHAR_SIZE];

	Trie()
	{
		this->isLeaf = false;
		for (int i = 0; i < CHAR_SIZE; i++)
			this->character[i] = nullptr;
	}

	void insert(std::string);
};

void Trie::insert(std::string key) {
	Trie* curr = this;
	for (int i = 0; i < key.length(); i++) {
        int k = key[i]=='.'?0:1;
		if (curr->character[k] == nullptr)
			curr->character[k] = new Trie();

		curr = curr->character[k];
	}
	if (!curr->isLeaf) n++;
    curr->isLeaf = true;
}

