        int n = S.length();
        for (int l = 1; l <= n / 2; ++l)
            if (S[0] == S[n - l] && S[l - 1] == S[n - 1])
                if (S.substr(0, l) == S.substr(n - l))
                    return longestDecomposition(S.substr(l, n - l - l), res + 2);
        return n ? res + 1 : res;
    }
