class Solution {
    const int Mod = int(1e9) + 123; // prime mod of polynomial hashing
    const int Base = 26;

    int helper(const string& s, int l, int r) {
        if (l >= r)
            return 0;
        // use rolling/polynomial hashes for prefix and suffix substrings
        long long prefix_hash = 0;
        long long suffix_hash = 0;
        long long pow = 1;
        for (int i = 0; i < (r - l) / 2; ++i) {
            // update hashes with the next letter
            prefix_hash = (prefix_hash + (s[l + i] - 'a') * pow) % Mod;
            suffix_hash = (suffix_hash * Base + (s[r - i - 1] - 'a')) % Mod;
            pow = (pow * Base) % Mod;
            // perform the expensive string check only for the equal hashes
            if (prefix_hash == suffix_hash) {
                if (s.substr(l, i + 1) == s.substr(r - i - 1, i + 1)) {
                    return 2 + helper(s, l + i + 1, r - i - 1);   
                }
            }
        }
        // there is no equal chunks
        return 1;
    }
public:
    int longestDecomposition(string text) {
        return helper(text, 0, text.size());
    }
};
```

