class Solution {
public:

    long long mod = 1e9 + 7;

    long long power(long long a, long long b) {
        long long res = 1;
        while(b) {
            if(b%2) res = (res%mod * a%mod)%mod , b-=1;
            b/=2;
            a = ((a%mod) * (a%mod))%mod;
        }

        return res%mod;
    }


    // precalculate hash of each prefix
    void generateHashes(string &text, vector<long long> &hash, vector<long long> &invModulo) {
        long long p = 31;
        long long p_power = 1;
        invModulo[0] = 1;
        hash[0] = (text[0] - 'a' + 1);

        for(int i=1; i < text.length(); i++) {
            char ch = text[i];
            p_power = (p_power%mod * p%mod)%mod;
            invModulo[i] = power(p_power, mod-2); // fermet's little theorem for calculating inverse modulo
            hash[i] = (hash[i-1]%mod + ((ch - 'a' + 1) * p_power%mod)%mod)%mod;
        }

    }

    // return hash of each substring in O(1)
    long long substringHash(int left, int right, vector<long long> &hash, vector<long long> &invModulo) {
        long long res = hash[right]%mod;
        if(left > 0) res = (res%mod -  hash[left - 1]%mod)%mod;

        return (mod + (res%mod * invModulo[left]%mod))%mod;
    }


    int longestDecomposition(string text) {
        int n = text.length();
        vector<long long> hash(n), invModulo(n);

        generateHashes(text, hash, invModulo);

        int start = 0, left = 0, right = n-1, end = n-1;
        int ans = 0;

        while(left < right) {

            // we found one substring matching so split from this point (we have to get the max number of times we can split the string (largest Possible value of k))
            if(substringHash(start, left, hash, invModulo) == substringHash(right, end, hash, invModulo)) {
                ans += 2; // because 2 parts are being created.

                start = left+1;
                end = right-1;
            } 

            left += 1;
            right -= 1;


        }

        if(start <= end) ans += 1; // if one of the substring is still remaining.

        return ans;
    }
};
