class Solution {
    public int longestDecomposition(String text) {
        if(text.length() == 0) return 0;

        int n = text.length();
        int[] dp = new int[n/2+2];

        int max = 0; 
        int maxIndex = -1;

        // i is length of the chunk 
        for(int i=1; i<=n/2; i++){ 
            for(int j=0; j<i; j++){
                if(text.substring(j, i).equals(text.substring(n-i, n-j))){
                    if(j == 0 || dp[j] != 0){
                        dp[i] = Math.max(dp[i], 2 + dp[j]);
                    }
                }
            }

            max = Math.max(max, dp[i]);
            if(dp[i] != 0) maxIndex = i;
        }

        if(maxIndex == -1) return 1; // There are no palindromes, take the entire string as one chunk
        if(n%2 == 0){
             /* 
                The length is even, if the palindrome is possible at the middle like abba then ab -> 2nd position (i.e. size/2), It
                will give the max chunks
                And if the palindrome decomposition is not possible in the middle, take the middle as one chunk and return max + 1
                like if the text is "abcdklmdcba", here we will take "klm" as one chunk 
             */
            if(maxIndex == n/2) return max;
            return max + 1;
        }

        // Max chunks plus the one chunk in the middle whose decomposition is not possible 
        return max + 1;
    }
}
