// Using Rabin Karp Algorithm
public int longestDecomposition(String text) {
	long hash1 = 0;
	long hash2 = 0;

	long mod = (long)Math.pow(2, 31);

	int l = 0;
	int r = text.length() - 1;

	long rightPow = 1;

	int count = 0;
	while(l < r) {            
		hash1 *= 26;
		hash1 += text.charAt(l) - 'a';
		hash1 %= mod;

		hash2 += (text.charAt(r) - 'a') * rightPow;
		hash2 %= mod;

		rightPow *= 26;
		rightPow %= mod;

		if(hash1 == hash2) {
			count += 2;

			hash1 = 0;
			hash2 = 0;
			rightPow = 1;
		}

		l++;
		r--;
	}

	if(hash1 != 0 || l == r || count == 0)
		count++;

	return count;
}
