var longestDecomposition = function(text) {
    let count = 0;
    let left = 0;
    let right = text.length - 1;
    let leftStr = '';
    let rightStr = '';
    while (left < right) {
        leftStr += text[left++];
        rightStr = text[right--] + rightStr;
        if (leftStr === rightStr) {
            count += 2;
            leftStr = '';
            rightStr = '';
        }
    }
    if (leftStr || left === right) count++;
    return count;
};
