class Solution {
    int mod=1000000007;
    long hash[];
    long pow[];
    int dp[][];
    public int longestDecomposition(String text) {
        dp=new int[text.length()+1][text.length()+1];
        hash=new long[text.length()];
        pow=new long[text.length()];
        pow[0]=1;
        hash[0]=text.charAt(0)%mod;
        for(int i=1;i<text.length();i++){
            hash[i]=(hash[i-1]*26+text.charAt(i))%mod;
            pow[i]=(pow[i-1]*26)%mod;
        }//seting up the hash
        int ans=dfs(text,0,text.length()-1);
        return ans;
    }

    public int dfs(String text,int left,int right){
        if(left==right)return 1;
        if(left>right)return 0;
        int res=1;
        for(int len=1;len<text.length();len++){
            long hash1=0;long hash2=0;
            int leftend=left+len-1;
            int rightstart=right-len+1;
            if(leftend>=rightstart){
                break;
            }
            hash1=gethash(text,left,leftend);
            hash2=gethash(text,rightstart,right);
            if(hash1==hash2){
                if(dp[leftend+1][rightstart-1]==0){
                   dp[leftend+1][rightstart-1]=dfs(text,leftend+1,rightstart-1); 
                }
                if(dp[leftend+1][rightstart-1]!=-1){
                    res=Math.max(res,2+dp[leftend+1][rightstart-1]);
                }
            }
        }
        return res;
    }
    public long gethash(String text,int left,int right){
        if(left==0)return hash[right];
        long res=(hash[right]-hash[left-1]*pow[right-left+1])%mod;
        if(res<0)res+=mod;
        return res;
    }
}
