class Solution {
public:
    int longestDecomposition(string& text) {
        string rtext(text.rbegin(), text.rend());   // reversed text
        map<char, int> cnts;
        int ret = 0, pos = 0, size = text.size();
        while (pos < size) {
            ++cnts[text[pos]];
            if (cnts[text[pos]] == 0) cnts.erase(text[pos]);
            --cnts[rtext[pos]];
            if (cnts[rtext[pos]] == 0) cnts.erase(rtext[pos]);
            if (cnts.empty()) ++ret;
            ++pos;
        }
        return ret;
    }
};
