class Solution {
public:
    int longestDecomposition(string& text) {
        map<char, int> cnts;
        int ret = 0, left = 0, right = text.size() - 1;
        while (left < right) {
            char ch = text[left];
            ++cnts[ch];
            if (cnts[ch] == 0) cnts.erase(ch);

            ch = text[right];
            --cnts[ch];
            if (cnts[ch] == 0) cnts.erase(ch);

            if (cnts.empty()) ret += 2;
            ++left;
            --right;
        }
        if (left == right || !cnts.empty()) ++ret;
        return ret;
    }
};
