class Solution {
public:

    int recursion(string &s, int l, int r){
        if(l > r) return 0;
        if(l == r) return 1;

        int boundary = (l + r - 1) / 2;
        int ans = 0;
        bool comp = false;
        for(int i = r; i > boundary; i--){
            bool flg = true;
            for(int j = 0; j <= (r-i); j++){
                if(s[l + j] != s[i + j]){
                    flg = false;
                    break;
                }
            }
            if(flg){
                comp = true;
                ans = max(ans, recursion(s, l + (r - i) + 1, r - (r - i) - 1));
            }
            if(flg) break;
        }
        if(!comp) return 1;
        return ans+2;
    }

    int longestDecomposition(string text) {
        return recursion(text, 0, text.size()-1);
    }
};
```

