class Solution {
    public int longestDecomposition(String text) {
        Map<Character,Integer> map1=new HashMap<>();
        Map<Character,Integer> map2=new HashMap<>();
        StringBuilder check=new StringBuilder(text);
        char[] str=text.toCharArray();
        int i=0,j=str.length-1;
        int count=0;
        int start=0;
        int end=str.length;
        while(i<j){
            map1.put(str[i],map1.getOrDefault(str[i],0)+1);
            map2.put(str[j],map2.getOrDefault(str[j],0)+1);
            if(map1.equals(map2)){
                if(check.substring(start,i+1).equals(check.substring(j,end))){
                    start=i+1;
                    end=j;
                    count+=2;
                    map1.clear();
                    map2.clear();
                } 
            }
            j--;
            i++;
        }
        if(str.length%2==0 && map1.size()==0) return count;
        return count+1;
    }
}
